import React, { useState } from "react";
import { Paper, Grid, IconButton, Button } from "@mui/material";
import VideocamIcon from "@mui/icons-material/Videocam";
import VideocamOffIcon from "@mui/icons-material/VideocamOff";
import MicIcon from "@mui/icons-material/Mic";
import MicOffIcon from "@mui/icons-material/MicOff";

function Stream(socket) {
  // 비디오 스트림 상태 배열 관리
  const [videoStreams, setVideoStreams] = useState([]);

  // 비디오 스트림 추가 핸들러
  const addVideoStream = () => {
    setVideoStreams(prevStreams => [...prevStreams, {}]);
  };

  return (
    <Paper elevation={3}>
      <Grid container direction="column" alignItems="center" justify="center">
        {videoStreams.map((stream, index) => (
          <Grid item xs={12} key={index}>
            {/* 화상 채팅 비디오 영역 */}
            <div
              style={{ width: "100%", height: "160px", backgroundColor: "#ddd", marginTop: "20px" }}
            >
              {/* 여기에 비디오 스트림을 렌더링 */}
              <video style={{width: "100%", height: "100%", objectFit: "cover"}}></video>
            </div>
          </Grid>
        ))}
        <Grid item xs={12}>
          {/* 비디오 스트림 추가 버튼 */}
          <Button variant="contained" color="primary" onClick={addVideoStream}>
            비디오 스트림 추가
          </Button>
        </Grid>
      </Grid>
    </Paper>
  );
}

export default Stream;
